package com.viettel.etc.controllers;import com.viettel.etc.dto.ActTypeMappingDTO;import com.viettel.etc.services.ActTypeMappingService;import com.viettel.etc.utils.exceptions.EtcException;import com.viettel.etc.xlibrary.core.constants.FunctionCommon;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.security.core.Authentication;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import com.viettel.etc.utils.*;import org.springframework.security.core.annotation.AuthenticationPrincipal;import javax.validation.Valid;/** * Autogen class: Chung to cho loai tac dong * * @author ToolGen * @date Tue Sep 08 09:29:20 ICT 2020 */@RestController@RequestMapping(Constants.REQUEST_MAPPING_V1 + "/act-type-mapping")public class ActTypeMappingController {    @Autowired    private ActTypeMappingService actTypeMappingService;    /**     * Lay danh sach chung tu cho loai tac dong     *     * @param authentication: thong tin nguoi dung     * @param dataParams      params client     * @return     */    @RequestMapping(produces = MediaType.APPLICATION_JSON_VALUE, method = RequestMethod.GET)    public ResponseEntity<Object> find(@AuthenticationPrincipal Authentication authentication, ActTypeMappingDTO dataParams) {        Object resultObj = actTypeMappingService.find(dataParams);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /***     * Them moi cau hinh giay to cho loai tac dong     * @param authentication :thong tin nguoi dung     * @param dataParams: params client     * @return     * @throws EtcException     * @throws Exception     */    @RequestMapping(method = RequestMethod.POST, produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> insert(@AuthenticationPrincipal Authentication authentication,                                         @RequestBody @Valid ActTypeMappingDTO dataParams) throws EtcException, Exception {        Object resultObj = actTypeMappingService.insert(dataParams, authentication);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /***     * Lay chi tiet cau hinh     * @param id     * @return     */    @RequestMapping(value = "/{id}", method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> findOne(@PathVariable(value = "id") Long id) {        Object resultObj = actTypeMappingService.findOne(id);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /***     * Cap nhat trang thai     * @param authentication : thong tin nguoi dung     * @param id : item can cap nhat     * @return     * @throws EtcException     * @throws Exception     */    @RequestMapping(value = "/update-status/{id}", method = RequestMethod.PUT, produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> updateStatus(@AuthenticationPrincipal Authentication authentication, @PathVariable(value = "id") Long id) throws EtcException, Exception {        Object resultObj = actTypeMappingService.updateStatus(id, authentication);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /***     * Xoa cau hinh     * @param id : item can xoa     * @return     * @throws EtcException     * @throws Exception     */    @RequestMapping(value = "/{id}", method = RequestMethod.DELETE, produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> delete(@PathVariable(value = "id") Long id) throws EtcException, Exception {        Object resultObj = actTypeMappingService.delete(id);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }}