package com.viettel.etc.controllers;import com.viettel.etc.dto.CustTypeMappingDTO;import com.viettel.etc.services.CustTypeMappingService;import com.viettel.etc.utils.Constants;import com.viettel.etc.utils.exceptions.EtcException;import com.viettel.etc.xlibrary.core.constants.FunctionCommon;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.security.core.Authentication;import org.springframework.security.core.annotation.AuthenticationPrincipal;import org.springframework.web.bind.annotation.*;import javax.validation.Valid;/** * Autogen class: Chung tu cho loai khach hang * * @author ToolGen * @date Tue Sep 08 11:35:08 ICT 2020 */@RestController@RequestMapping(Constants.REQUEST_MAPPING_V1 + "/cust-type-mapping")public class CustTypeMappingController {    @Autowired    private CustTypeMappingService custTypeMappingService;    /**     * Lay danh sach chung tu cho loai khach hang     *     * @param authentication: thong tin nguoi dung     * @param dataParams      params client     * @return     */    @RequestMapping(produces = MediaType.APPLICATION_JSON_VALUE, method = RequestMethod.GET)    public ResponseEntity<Object> find(@AuthenticationPrincipal Authentication authentication, CustTypeMappingDTO dataParams) {        Object resultObj = custTypeMappingService.find(dataParams);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /***     * them moi cau hinh     * @param authentication: thong tin nguoi dung     * @param dataParams: params client     * @return     * @throws EtcException     * @throws Exception     */    @RequestMapping(method = RequestMethod.POST, produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> insert(@AuthenticationPrincipal Authentication authentication,                                         @RequestBody @Valid CustTypeMappingDTO dataParams) throws EtcException, Exception {        Object resultObj = custTypeMappingService.insert(dataParams, authentication);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /***     * xem chi tiet cau hinh     * @param id : id item can cap nhat     * @return     */    @RequestMapping(value = "/{id}", method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> findOne(@PathVariable(value = "id") Long id) {        Object resultObj = custTypeMappingService.findOne(id);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /***     * cap nhat trang thai     * @param authentication : thong tin nguoi dung     * @param id : id item can cap nhat     * @return     * @throws EtcException     * @throws Exception     */    @RequestMapping(value = "/update-status/{id}", method = RequestMethod.PUT, produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> updateStatus(@AuthenticationPrincipal Authentication authentication, @PathVariable(value = "id") Long id) throws EtcException, Exception {        Object resultObj = custTypeMappingService.updateStatus(id, authentication);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /***     * xoa cau hinh     * @param id Ma dinh danh loai chung tu khac hang     * @return     * @throws EtcException     * @throws Exception     */    @RequestMapping(value = "/{id}", method = RequestMethod.DELETE, produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> delete(@PathVariable(value = "id") Long id) throws EtcException, Exception {        Object resultObj = custTypeMappingService.delete(id);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }}