package com.viettel.etc.controllers;import com.viettel.etc.dto.SaleTransDTO;import com.viettel.etc.dto.SaleTransVehicleOwnerAppDTO;import com.viettel.etc.services.SaleTransService;import com.viettel.etc.utils.Constants;import com.viettel.etc.utils.FnCommon;import com.viettel.etc.xlibrary.core.constants.FunctionCommon;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.security.core.Authentication;import org.springframework.security.core.annotation.AuthenticationPrincipal;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.servlet.http.HttpServletResponse;import java.io.IOException;/** * Autogen class: Lop thao tac danh sach lich su * * @author ToolGen * @date Fri Jul 10 10:48:45 ICT 2020 */@RestController@RequestMapping(Constants.REQUEST_MAPPING_V1)public class SaleTransController {    @Autowired    private SaleTransService saleTransService;    /**     * Lay du lich su giao dich khac     *     * @param authentication: thong tin nguoi dung     * @param saleTransDTO    params client     * @return     */    @GetMapping(value = "/contracts/{id}/other-transaction-histories", produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> findOtherTransactionHistories(@AuthenticationPrincipal Authentication authentication,                                                                @PathVariable(value = "id") Long contractId,                                                                SaleTransDTO saleTransDTO) {        Object resultObj = saleTransService.findOtherTransactionHistories(contractId, saleTransDTO);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /**     * Export lich su giao dich khac     *     * @param authentication: thong tin nguoi dung     * @param saleTransDTO    params client     * @return     */    @GetMapping(value = "/contracts/{id}/other-transaction-histories/export", produces = MediaType.APPLICATION_JSON_VALUE)    public void exportOtherTransactionHistories(@AuthenticationPrincipal Authentication authentication,                                                @PathVariable(value = "id") Long contractId,                                                HttpServletResponse response,                                                SaleTransDTO saleTransDTO) throws IOException {        String fileName = saleTransService.exportOtherTransactionHistories(contractId, saleTransDTO);        FnCommon.responseFile(response, fileName);    }    /**     * Lay danh sach loai giao dich     *     * @param authentication: thong tin nguoi dung     * @param dataParams      params client     * @return     */    @GetMapping(value = "/services-fees", produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> getServiceFees(@AuthenticationPrincipal Authentication authentication,                                                 SaleTransDTO dataParams) {        Object resultObj = saleTransService.getServiceFees(dataParams);        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }    /**     * Lay du lich su giao dich khac - app CPT     *     * @param authentication:         thong tin nguoi dung     * @param transVehicleOwnerAppDTO params client     * @return     */    @GetMapping(value = Constants.MOBILE + "/other-transaction-histories", produces = MediaType.APPLICATION_JSON_VALUE)    public ResponseEntity<Object> vehicleOwnerAppOtherTransactionHistories(@AuthenticationPrincipal Authentication authentication,                                                                           SaleTransVehicleOwnerAppDTO transVehicleOwnerAppDTO) {        Object resultObj = saleTransService.vehicleOwnerAppOtherTransactionHistories(transVehicleOwnerAppDTO, FnCommon.getUserLogin(authentication));        return new ResponseEntity<>(FunctionCommon.responseToClient(resultObj), HttpStatus.OK);    }}