package com.viettel.etc.repositories.impl;import com.viettel.etc.dto.AttachmentFileDTO;import com.viettel.etc.repositories.AttachmentFileRepository;import com.viettel.etc.xlibrary.core.repositories.CommonDataBaseRepository;import org.springframework.stereotype.Repository;import java.util.ArrayList;import java.util.List;/** * Autogen class Repository Impl: Thong tin tep dinh kem * * @author ToolGen * @date Fri Jul 03 10:59:24 ICT 2020 */@Repositorypublic class AttachmentFileRepositoryImpl extends CommonDataBaseRepository implements AttachmentFileRepository {    /**     * API lay file     *     * @param id: params client truyen len     * @return     */    @Override    public List<AttachmentFileDTO> getFileByAttachId(Long id) {        StringBuilder sql = new StringBuilder();        List<Object> arrParams = new ArrayList<>();        sql.append("select ATTACHMENT_FILE_ID attachmentFileId, ATTACHMENT_TYPE attachmentType,");        sql.append(" OBJECT_ID objectId, DOCUMENT_NAME documentName, DOCUMENT_PATH documentPath, description, status");        sql.append(" from ATTACHMENT_FILE where ATTACHMENT_FILE_ID = ? \n");        arrParams.add(id);        return (List<AttachmentFileDTO>) getListData(sql, arrParams, null, null, AttachmentFileDTO.class);    }}