package com.viettel.etc.repositories.tables;import com.viettel.etc.repositories.tables.entities.SaleTransDetailEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.sql.Date;import java.util.List;/** * Autogen class Repository Interface: Create Repository For Table Name Sale_trans_detail * * @author ToolGen * @date Wed Jun 24 11:57:24 ICT 2020 */@Repositorypublic interface SaleTransDetailRepositoryJPA extends JpaRepository<SaleTransDetailEntity, Long> {    List<SaleTransDetailEntity> findByVehicleIdAndStatus(Long vehicleId, Long status);    List<SaleTransDetailEntity> findByPlateNumberAndEpcAndStatus(String plateNumber, String epc, Long status);    List<SaleTransDetailEntity> findByPlateNumberAndEpcAndStatusNotAndEffDateLessThanEqualAndExpDateGreaterThanEqual(String plateNumber, String epc, Long status, Date startDate, Date endDate);    SaleTransDetailEntity findBySubscriptionTicketId(Long subscriptionTicketId);    @Query("Select std from SaleTransDetailEntity std where std.orgPlateNumber = :orgPlateNumber and std.epc = :epc and (std.expDate is null or std.expDate >= current_date)  and std.status = 2 and std.booCode = :booCode")    List<SaleTransDetailEntity> findOrgPlateTypeNumberAndEpcAndBooCode(String orgPlateNumber, String epc, String booCode);    @Query("Select std from SaleTransDetailEntity std where std.orgPlateNumber = :orgPlateNumber and std.epc = :epc and (std.expDate is null or std.expDate >= current_date)  and std.status = 2 and std.booFlow = :booFlow")    List<SaleTransDetailEntity> findOrgPlateTypeNumberAndEpcAndBooFlow(String orgPlateNumber, String epc, Long booFlow);}