package com.viettel.etc.repositories.tables;import com.viettel.etc.repositories.tables.entities.VehicleEntity;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import org.springframework.stereotype.Repository;import java.util.List;import java.util.Optional;/** * Autogen class Repository Interface: Create Repository For Table Name Vehicle * * @author ToolGen * @date Wed Jun 24 11:57:24 ICT 2020 */@Repositorypublic interface VehicleRepositoryJPA extends JpaRepository<VehicleEntity, Long> {    List<VehicleEntity> findAllByContractIdInAndStatusAndActiveStatus(List<Long> Ids, String status, String activeStatus);    VehicleEntity findByVehicleId(Long vehicleId);    List<VehicleEntity> getByContractIdAndActiveStatusNot(Long contractId, String activeStatus);    List<VehicleEntity> findAllByPlateNumberAndStatusIsNot(String plateNumber, String status);    List<VehicleEntity> findByRfidSerial(String serial);    VehicleEntity findByVehicleIdAndStatusAndRfidSerial(Long vehicleId, String status, String rfidSerial);    List<VehicleEntity> findByContractIdInAndStatusAndActiveStatusIn(List<Long> contractIds, String status, List<String> activeStatus);    List<VehicleEntity> findAllByStatusAndRfidSerial(String status, String rfidSerial);    List<VehicleEntity> findAllByPlateNumber(String plateNumber);    List<VehicleEntity> findAllByPlateNumberAndStatus(String plateNumber, String status);    List<VehicleEntity> getByPlateNumberAndPlateTypeCodeAndStatusNot(String plateNumber, String plateTypeCode, String status);    List<VehicleEntity> getByPlateNumberAndStatusAndActiveStatusInAndPlateTypeCode(String plateNumber, String status, List<String> activeStatuses, String plateTypeCode);    VehicleEntity findByContractIdAndPlateNumberAndPlateTypeCode(Long contractid, String plateNumber, String plateTypeCode);    Optional<VehicleEntity> findByPlateNumberAndActiveStatusNot(String plateNumber, String activeStatus);    List<VehicleEntity> findByEpcAndPlateNumberAndActiveStatusAndStatus(String epc, String plateNumber, String activeStatus, String status);    List<VehicleEntity> findByEpcAndPlateNumberAndActiveStatusAndStatusAndContractId(String epc, String plateNumber, String activeStatus, String status, Long contractId);    @Query(value = "SELECT CRM.SERIAL_SEQ.nextval FROM dual", nativeQuery = true)    Long getNextValSequenceSerial();    boolean existsByPlateNumberAndStatusAndActiveStatusInAndContractIdNotAndPlateTypeCode(String plateNumber, String status, List<String> activeStatuses, Long contractId, String plateTypeCode);    boolean existsByPlateNumberAndStatusAndActiveStatusAndContractIdAndPlateTypeCode(String plateNumber, String status, String activeStatus, Long contractId, String plateTypeCode);    @Query(value = "SELECT veh FROM VehicleEntity veh where veh.plateNumber = :plateNumber and veh.status = '1' and veh.activeStatus in ('1','4') ")    Optional<VehicleEntity> findByPlateNumberActive(String plateNumber);    @Query(value = "select MAX(VEHICLE_GROUP_ID) from VEHICLE where CONTRACT_ID = :contractId ", nativeQuery = true)    Long getHighestPriceVehicle(Long contractId);    @Query(value = "SELECT veh FROM VehicleEntity veh where veh.plateNumber = :plateNumber and veh.custId <> 1 and veh.contractId <> 1")    List<VehicleEntity> findAllByPlateNumberNotBOO1(String plateNumber);    List<VehicleEntity> findByPlateNumberAndPlateTypeCodeAndActiveStatusAndStatus(String plateNumber, String plateTypeCode, String status, String activeStatus);    @Query(value = "SELECT veh FROM VehicleEntity veh where ((veh.status = '1' and veh.activeStatus in ('1','3','4','5'))  or (veh.status = '0' and veh.activeStatus = '2')) and veh.contractId = :contractId")    List<VehicleEntity> findAllByContractIdAndStatusAndActiveStatus(Long contractId);}