package com.viettel.etc.services.tables;import com.viettel.etc.repositories.tables.entities.PromotionEntity;import com.viettel.etc.repositories.tables.PromotionRepositoryJPA;import java.util.List;import java.util.Optional;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * Autogen class: Create Service For Table Name Promotion *  * @author ToolGen * @date Fri Sep 04 15:40:37 ICT 2020 */@Servicepublic class PromotionServiceJPA {    @Autowired    PromotionRepositoryJPA promotion;    public List<PromotionEntity> findAll() {        return this.promotion.findAll();    }    public PromotionEntity save(PromotionEntity Promotion) {        return this.promotion.save(Promotion);    }    public List<PromotionEntity> getByIdIn(List<Long> listId) {        return promotion.getByPromotionIdIn(listId);    }    public List<PromotionEntity> saveAll(List<PromotionEntity> promotionList) {        return promotion.saveAll(promotionList);    }    public Optional<PromotionEntity> findById(Long id) {        return this.promotion.findById(id);    }    public void deleteById(Long id) {        this.promotion.deleteById(id);    }    public PromotionEntity getOne(Long id) {        return this.promotion.getOne(id);    }    public Boolean existsById(Long id) {        return this.promotion.existsById(id);    }}